    private void submeterActionPerformed(java.awt.event.ActionEvent evt) {
        javax.swing.JFrame tp;
        if (criarPasseTurista()){
            passes.adicionarPasse(new EpassTurista(duracao));
            tp = new PasseCriado(passes);
        }
        else{tp = new PasseNaoCriado(passes); }
        tp.setVisible(true);
        tp.pack();
        tp.setLocationRelativeTo(null);
        tp.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);
        this.dispose();
    }

