    private boolean criarPasseTurista(){
        String escolha = jComboBox1.getSelectedItem().toString();
        switch (escolha) {
            case "1" -> duracao = Duracao.Um;
            case "3" -> duracao = Duracao.Tres;
            case "7" -> duracao = Duracao.Sete;
            }
            saldo = Double.parseDouble(TextField.getText());
            return true;
        }

